import React, { Component } from 'react';
import '../App.css'
import {Bar} from 'react-chartjs-2';
import Chart from 'chart.js'
import ChartComponent from './chartComponent'

class Home extends Component {
    constructor(props){
        super(props)

        this.state={
            data: [],
			series: ['France', 'Italy', 'England', 'Sweden', 'Germany'],
			labels: ['cats', 'dogs', 'horses', 'ducks', 'cows'],
			colors: ['#43A19E', '#7B43A1', '#F2317A', '#FF9824', '#58CF6C']
        }
    }

    componentDidMount() {
		this.populateArray();
		setInterval(this.populateArray, 2000);
    }

    getRandomInt(min, max) {
        return Math.floor(Math.random() * (max - min)) + min;
    }

	populateArray() {
		let data = [],
			series = 5,//getRandomInt(2, 10),
			serieLength = 5;//getRandomInt(2, 10);

		for (var i = series; i--; ) {
			var tmp = [];
			for (var j = serieLength; j--; ) {
				tmp.push(this.getRandomInt(0, 20));
			}
			data.push(tmp);			
		}

		this.setState({ data: data });
    }

    render() {
        return (
            <div className="Container">
                <div className="App-header">
                    <p>tugas big data analytics</p>
                </div>
                <div className="Body"> 

                <ChartComponent data={ this.state.data }
					labels={ this.state.series }
					colors={ this.state.colors }
                    // height={ 250 }
                    width={800}
                />
                </div>
            </div>
        );
    }
}

export default Home
