import React, { Component } from "react";
import "../App.css";
import { Bar } from "react-chartjs-2";
import axios from "axios";
import _ from "lodash";

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      labels: ["twitter stream"],
      datasets: [
        {
          label: "sentimen positif",
          backgroundColor: "rgba(75,192,192,1)",
          data: [55]
        },
        {
          label: "sentimen netral",
          backgroundColor: "rgb(228,227,115)",
          data: [65]
        },
        {
          label: "sentimen negatif",
          backgroundColor: "rgb(227,21,55)",
          data: [70]
        }
      ],
      data: []
    };
  }

  setInterval = () => {
    setInterval(this.getData, 10000);
  };

  getData = () => {
    const urlData = "https://bigdataanalytic.herokuapp.com/sentiment";
    let data = [];
    axios
      .get(
        urlData,
        {},
        {
          headers: {
            "content-type": "application/json"
          }
        }
      )
      .then(response => {
        if (response.status === 200 && response.data !== "undefined") {
          data.push(response.data);
          this.getDataResponse(data);
        }
      })
      .catch(function(err) {
        return console.log("error get data", err);
      });
  };

  componentDidMount() {
    this.getData();
  }

  getDataResponse = value => {
    let dataSet = this.state;
    let tempData = [],
      dataFromApiTemp = [];
    _.map(value, v => {
      _.map(v, item => {
        dataFromApiTemp.push(item.value);
      });
    });

    let dataKosong = [...dataFromApiTemp];
    _.map(dataKosong, (key, value) => {
      if (!!dataKosong) {
        switch (key) {
          case "1":
            console.log("cek banyak data 1", value);
            break;
          case "-1":
            console.log("cek banyak data -1", value);
            break;
          default:
            console.log("cek banyak data 0", value);;
        }
      }
    });
    return dataSet;
  };

  render() {
    console.log("set data", this.getDataResponse);
    return (
      <div className="Container">
        <div className="App-header">
          <p>Tugas big data analytics</p>
        </div>
        <div className="Body">
          <Bar
            data={this.getDataResponse()}
            options={{
              title: {
                display: true,
                text: "Sentimen Analisis BDA",
                fontSize: 20
              },
              legend: {
                display: true,
                position: "bottom"
              }
            }}
          />
        </div>
        {/* <Legend labels={ this.state.labels } colors={ this.state.colors } /> */}
      </div>
    );
  }
}

export default Home;
