import React, { Component } from 'react';
import '../App.css'
import {Bar} from 'react-chartjs-2';

class Home extends Component {
    constructor(props){
        super(props)

        this.state={
            labels: ['twitter stream'],
            datasets: [
                {
                    label: 'sentimen positif',
                    backgroundColor: 'rgba(75,192,192,1)',
                    data: [55]
                },
                {
                    label: 'sentimen netral',
                    backgroundColor: 'rgb(228,227,115)',
                    data: [65]
                },
                {
                    label: 'sentimen negatif',
                    backgroundColor: 'rgb(227,21,55)',
                    data: [70]
                }
            ],
            width: 500
        }
    }

    loadCached() {
    const urlData = 'https://bigdataanalytic.herokuapp.com/sentiment'
    let cache = new Map();

    if (cache.has(urlData)) {
        return Promise.resolve(cache.get(urlData)); // (*)
    }

    return fetch(urlData)
        .then(text => {
            cache.set(urlData,text);
            return text;
        })
        .catch(err => console.log("error get promise", err))
    }

    render() {
        console.log("cek promise", this.loadCached())
        return (
            <div className="Container">
                <div className="App-header">
                    <p>Tugas big data analytics</p>
                </div>
                <div className="Body"> 
                <Bar
                    data={this.state}
                    options={{
                        title:{
                            display:true,
                            text:'Sentimen Analisis BDA',
                            fontSize:20
                        },
                        legend:{
                            display:true,
                            position:'bottom'
                        }
                    }}
                />            
                </div>
                {/* <Legend labels={ this.state.labels } colors={ this.state.colors } /> */}
            </div>
        );
    }
}

export default Home
