import React, { Component } from 'react';
import '../App.css'
import {Bar} from 'react-chartjs-2';
import Chart from 'chart.js'
import ChartComponent from './chartComponent'

class Home extends Component {
    constructor(props){
        super(props)

        this.state={
            datasets: [{
                // barPercentage: 0.5,
                // barThickness: 6,
                // maxBarThickness: 8,
                // minBarLength: 2,
                data: [10, 20, 30, 40, 50, 60, 70]
            }]
        }
    }
    render() {
        return (
            <div className="Container">
                <div className="App-header">
                    <p>tugas big data analytics</p>
                </div>
                <div className="Body"> 
                {/* <p>hai ini content</p> */}
                <div style={{width: '700px', height: '700px', marginTop: '30px'}}>
                    <Bar type="horizontalBar" data={this.state.datasets[0].data}> 
                    </Bar>
                </div>

                {/* <ChartComponent 

                /> */}
                </div>
            </div>
        );
    }
}

export default Home

const rand = (min, max, num) => {
    let rtn = [];

    while (rtn.length < num) {
      rtn.push((Math.random() * (max - min)) + min);
    }

    return rtn;
}

const data = {
    1: {
      labels: ["January", "February", "March", "April", "May", "June", "July"],
      datasets: [
        {
          label: "My First dataset",
          fillColor: "rgba(220,220,220,0.2)",
          strokeColor: "rgba(220,220,220,1)",
          pointColor: "rgba(220,220,220,1)",
          pointStrokeColor: "#fff",
          pointHighlightFill: "#fff",
          pointHighlightStroke: "rgba(220,220,220,1)",
          data: rand(32, 100, 7)
        },
        {
          label: "My Second dataset",
          fillColor: "rgba(151,187,205,0.2)",
          strokeColor: "rgba(151,187,205,1)",
          pointColor: "rgba(151,187,205,1)",
          pointStrokeColor: "#fff",
          pointHighlightFill: "#fff",
          pointHighlightStroke: "rgba(151,187,205,1)",
          data: rand(32, 100, 7)
        }
      ]
    },

    // add more data bellow and use it to render more charts
    2: {
      labels: [],
      datasets: []
    },

    3: {
      labels: [],
      datasets: []
    }
};
